<?phpinclude './Connect.php';global $conn;if($_SERVER['REQUEST_METHOD']=== 'POST' || isset($_POST['Display'])){    $TR = "";    $Query = "SELECT * FROM residencerecord";    $result = mysqli_query($conn,$Query);    if(mysqli_num_rows($result) > 0){        while ($row = mysqli_fetch_assoc($result)){            $FirsName = $row['FirsName'];            $LastName = $row['LastName'];            $MiddleName = $row['MiddleName'];            $Age = $row['Age'];            $Gender = $row['Gender'];            $ResidenceProfile = $row['ResidenceProfile'];            $Voters = $row['Voters'];            $Blk = $row['Blk'];            $House_Numbe = $row['House_Numbe'];            $Gmail = $row['Gmail'];            $Citizenship = $row['Citizenship'];            $Birthday = $row['Birthday'];            $Id = $row['ID'];        $TR .= "<tr>";         $TR .= "<td>$Id</td>";         $TR .= "<td><img  class='img-thumbnail rounded-4' src='../Model/ResidenceProfileRecord/$ResidenceProfile' alt='profile'></td>";         $TR .= "<td>$FirsName</td>";         $TR .= "<td>$LastName</td>";         $TR .= "<td>$MiddleName</td>";         $TR .= "<td>$Birthday</td>";         $TR .= "<td>$Age</td>";         $TR .= "<td>$Blk</td>";         $TR .= "<td>$Gender</td>";         $TR .= "<td>$Voters</td>";         $TR .= "</tr>";        }    }    echo $TR;}