<?phpinclude "./Connect.php";global $conn;$Query = "SELECT Id ,FirstName,LastName,MiddleName,Gender,Position FROM barrangayofficials";$result = mysqli_query($conn,$Query);if(mysqli_num_rows($result) > 0){    $Tr = "";    while ($row = mysqli_fetch_assoc($result)){        $Id = $row['Id'];        $First_name = $row['FirstName'];        $last_name = $row['LastName'];        $Middle_name = $row['MiddleName'];        $Gender = $row['Gender'];        $position = $row['Position'];        $Tr .= "<tr>";        $Tr .= "<th scope='row'>$Id</th>";        $Tr .= "<td>$First_name</td>";        $Tr .= "<td>$last_name</td>";        $Tr .= "<td>$Middle_name</td>";        $Tr .= "<td>$Gender</td>";        $Tr .= "<td>$position</td>";        $Tr .= "<td>                    <button id='update_btn' value='$Id' class='btn btn-warning'>                        <i class='fa-solid fa-pen-to-square'></i>                    </button>                    <button id='View_btn' value='$Id' class='btn btn-success'>                        <i  class='fa-solid fa-eye'></i>                    </button>               </td>";        $Tr .= "</tr>";    }    echo  $Tr;}