<?phpinclude './Connect.php';global $conn;if($_SERVER['REQUEST_METHOD'] === "POST" && $_POST['Id']){    $Id =  $_POST['Id'];    $Query = "SELECT Status FROM requesteddocuments WHERE ID = $Id ";    $result = mysqli_query($conn,$Query);    while ($row = mysqli_fetch_assoc($result)){        ?>        <!-- Modal -->        <div class="modal fade" id="Update_docs" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">            <div class="modal-dialog modal-dialog-centered">                <div class="modal-content">                    <div style="background-color: #142D55" class="modal-header">                        <h1 class="modal-title fs-5 text-light" id="exampleModalLabel">Update Status</h1>                        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>                    </div>                    <div class="modal-body">                        <select id="Status_UpdateValue" class="form-select" aria-label=" select example">                            <option selected><?php echo $row['Status'] ?></option>                            <option value="Pending">Pending</option>                            <option value="Processing">Processing</option>                            <option value="Ready to Pick-Up">Ready to Pick-Up</option>                            <option value="Released">Released</option>                        </select>                    </div>                    <div class="modal-footer">                        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>                        <button style="background-color: #142D55" id="save_btn" value="<?php echo $Id ?>" type="button" class="btn text-light">Save</button>                    </div>                </div>            </div>        </div><?php    }}