<!doctype html><html lang="en"><head>    <meta charset="UTF-8">    <meta name="viewport"          content="width=device-width, user-scalable=no, initial-scale=1.0, maximum-scale=1.0, minimum-scale=1.0">    <meta http-equiv="X-UA-Compatible" content="ie=edge">    <link rel="stylesheet" href="../css/bootstrap.min.css">    <link rel="stylesheet" href="../Main.css">    <script src="../js/bootstrap.bundle.min.js"></script>    <script src="https://cdn.jsdelivr.net/npm/sweetalert2@11"></script>    <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.7.1/jquery.min.js"></script>    <script src="https://kit.fontawesome.com/d4532539ca.js" crossorigin="anonymous"></script>    <title>Residence Records</title>    <style>        body{            font-family: 'poppins',sans-serif;            background-color: #142D55;        }        td img {            width: 50px;            height: 50px;            border-radius: 50%;        }    </style></head><body ><section class="container mt-5 p-5">    <h5 class="text-light">List of Residence</h5>    <div class="container d-lg-flex d-md-flex justify-content-between align-items-center p-3">        <nav aria-label="breadcrumb">            <ol class="breadcrumb">                <li class="breadcrumb-item"><a class="text-light" href="./Dashboard.php">Dashboard</a></li>                <li class="breadcrumb-item active text-light" aria-current="page">Residence Records </li>            </ol>        </nav>        <button class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#AddingResidence">            <i class="fa-solid fa-plus"></i>             Residence        </button>    </div>    <table class="table table-bordered table-primary  table-borderless">        <thead>        <tr>            <th scope="col">ResidentId</th>            <th scope="col">Profile</th>            <th scope="col">First Name</th>            <th scope="col">Last Name</th>            <th scope="col">Middle Name</th>            <th scope="col">Birthday</th>            <th scope="col">Age</th>            <th scope="col">Blk</th>            <th scope="col">Gender</th>            <th scope="col">Voters Status</th>        </tr>        </thead>        <tbody id="residentInfo">        </tbody>    </table></section><!--Modal for Adding Residence--><div class="modal fade" id="AddingResidence" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">    <div class="modal-dialog modal-dialog-centered modal-xl modal-dialog-scrollable">        <div class="modal-content">            <div style="background-color: #142D55" class="modal-header">                <h1 class="modal-title fs-5 text-light" id="exampleModalLabel">Resident Personal Information</h1>                <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>            </div>            <div class="modal-body d-flex flex-column gap-3">                <div>                    <label for="Profile"></label>                    <input  class="form-control" type="file" id="Profile">                </div>                <div>                     <label for="FirstName">FirstName</label>                     <input class="form-control" type="text" id="FirstName" placeholder="FirstName">                </div>                <div>                     <label for="LastName">LastName</label>                     <input class="form-control" type="text" id="LastName" placeholder="LastName">                </div>                <div>                     <label for="MiddleName">Middle Name</label>                     <input class="form-control" type="text" id="MiddleName" placeholder="Middle Name">                </div>                <div>                     <label for="Birthday">Birthday</label>                     <input class="form-control" type="date" id="Birthday">                </div>                <div>                     <label for="Age">Age</label>                     <input class="form-control" type="number" id="Age" placeholder="Age">                </div>                <select id="Resident_Gender" class="form-select" aria-label="Default select example">                    <option selected>Gender</option>                    <option value="Male">Male</option>                    <option value="Female">Female</option>                </select>                <select id="Resident_Blk" class="form-select" aria-label="Default select example">                    <option selected>Streets/Black</option>                    <option value="Blk 1">Blk 1</option>                    <option value="Blk 2">Blk 2</option>                    <option value="Blk 3">Blk 3</option>                    <option value="Blk 4">Blk 4</option>                    <option value="Blk 5">Blk 5</option>                    <option value="Blk 6">Blk 6</option>                    <option value="Blk 7">Blk 7</option>                    <option value="Blk 8">Blk 8</option>                    <option value="Blk 9">Blk 9</option>                </select>                <select id="Resident_Voters" class="form-select" aria-label="Default select example">                    <option selected>Voters</option>                    <option value="Yes">Yes</option>                    <option value="No">No</option>                </select>                <select id="Civil_Status" class="form-select" aria-label="Default select example">                    <option selected>Civil Status</option>                    <option value="Meried">Meried</option>                    <option value="Single">Single</option>                </select>                <div>                    <label for="Citizenship">Citizenship</label>                    <input class="form-control" type="text" id="Citizenship" placeholder="Citizenship">                </div>                <div>                    <label for="ContactNumber">Contact Number</label>                    <input class="form-control" type="tel" id="ContactNumber" placeholder="Contact Number">                </div>                <div>                    <label for="Gmail">Gmail Address</label>                    <input class="form-control" type="email" id="Gmail" placeholder="Gmail Address">                </div>                <div>                    <label for="Occupation">Occupation</label>                    <input class="form-control" type="text" id="Occupation" placeholder="Occupation">                </div>                <select id="HouseholdNumber" class="form-select" aria-label="Default select example">                    <option selected>Household Number</option>                    <option value="53472">53472</option>                    <option value="453234">453234</option>                    <option value="765453">765453</option>                    <option value="645453">645453</option>                </select>            </div>            <div class="modal-footer">                <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>                <button id="Add_Resident" style="background-color: #142D55" type="button" class="btn text-light">Add Resident</button>            </div>        </div>    </div></div><script src="../Main_JS/ResidenceRecord.js"></script></body></html>