<?phpinclude  './Connect.php';global $conn;if($_SERVER['REQUEST_METHOD'] === 'POST'){    $FilePath = $_FILES['Updated_image']['tmp_name'];    $Filename = $_FILES['Updated_image']['name'];    $FileDir = './Product_Images/';    $Destination = $FileDir . $Filename;    move_uploaded_file($FilePath,$Destination);    $image_name = htmlspecialchars(basename($_FILES['Updated_image']['name']));    $UpdatedProductName = $_POST['UpdatedProductName'];    $UpdatedProductPrice = $_POST['UpdatedProductPrice'];    $Description = $_POST['Description'];    $saveBtn = $_POST['saveBtn'];    $Query = "UPDATE  products SET ProductName = '$UpdatedProductName',Description = '$Description', Price = '$UpdatedProductPrice',Product_image = '$image_name' WHERE ID = $saveBtn";    mysqli_query($conn,$Query);}