<?phpinclude './Connect.php';global $conn;if($_SERVER['REQUEST_METHOD'] === 'POST' || isset($_POST['id'])){    $ID = $_POST['id'];    $Query = "SELECT ProductName,Description,Price,Product_image FROM products WHERE ID = $ID";    $result = mysqli_query($conn,$Query);    if(mysqli_num_rows($result) > 0){        while ($row = mysqli_fetch_assoc($result)){          ?>            <div class="modal fade" tabindex="-1" id="ViewUpdateProducts">                <div class="modal-dialog modal-lg modal-dialog-centered">                    <div class="modal-content">                        <div class="modal-header">                            <h5 class="modal-title fw-bold">Update Product</h5>                            <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>                        </div>                        <div class="modal-body d-flex flex-column gap-2">                            <img class="col-2 rounded-4" src="../Model/Product_Images/<?php echo $row['Product_image']?>" alt="image">                            <div>                                <label for="UpdatedProductImage">Product Image</label>                                <input src="../Model/Product_Images/<?php echo $row['Product_image']?>" accept="image/png, image/jpeg" class="form-control" type="file" id="UpdatedProductImage">                            </div>                             <div>                                <label for="UpdatedProductName">Product Name</label>                                <input class="form-control" value="<?php echo $row['ProductName']?>" type="text" id="UpdatedProductName">                            </div>                            <div>                                <label for="UpdatedProductPrice">Price</label>                                <input class="form-control" value="<?php echo $row['Price']?>" type="number" id="UpdatedProductPrice">                            </div>                            <div class="form-floating">                                <textarea class="form-control"  placeholder="Description" id="Description_" style="height: 100px" ><?php echo $row['Description']?></textarea>                                <label for="Description_">Description</label>                            </div>                        </div>                        <div class="modal-footer">                            <button type="button" class="btn btn-danger" data-bs-dismiss="modal">Close</button>                            <button id="SaveProductUpdated" value="<?php echo $ID?>" style="background-color: #142D55" type="button" class="btn btn-primary">Save</button>                        </div>                    </div>                </div>            </div><?php        }    }}