<?phpinclude "../../Database/Connect.php";global $conn;if($_SERVER['REQUEST_METHOD'] === 'POST'){    $ID = $_POST['ID'];    $Query = "SELECT OrderStatus from orders where id = $ID";    $result = mysqli_query($conn,$Query);    while ($row = mysqli_fetch_assoc($result)){        ?>        <select id="UpdatedStatus" aria-label="Default select example">            <option value="<?php echo $row['OrderStatus']?>" selected><?php echo $row['OrderStatus']?></option>            <option value="Received">Received</option>            <option value="Cancel">Cancel</option>            <option value="Processing">Processing</option>        </select>        <button id="Save" value="<?php echo $ID?>">Save</button><?php    }}