<!DOCTYPE html><html lang="en"><head>    <meta charset="UTF-8">    <meta name="viewport" content="width=device-width, initial-scale=1.0">    <title>Orders</title>    <link rel="stylesheet" href="../css/style.css"></head><body><section class="food-search">    <div class="container">        <h2 style="color: #2f3542" class="text-center">Fill this form to confirm your order.</h2>        <?php        if(isset($_POST['btnOrder'])){            include '../../Database/Connect.php';            global $conn;            $ID = $_POST['btnOrder'];            $Query = "SELECT * FROM products WHERE ID = $ID";            $result = mysqli_query($conn, $Query);            while ($row = mysqli_fetch_assoc($result)){                ?>                <form action="./AddOrders.php" class="order" method="post">                    <fieldset>                        <legend>Selected Food</legend>                        <div class="food-menu-img">                            <img style="border-radius: 10px" width="100" src="../../Admin/Model/Product_Images/<?php echo $row['Product_image'] ?>" alt="Product">                        </div>                        <div class="food-menu-desc">                            <label for="ProductName" class="order-label">Product Name</label>                            <input id="ProductName" class="input-responsive" name="ProductName" type="text" value="<?php echo $row['ProductName']?>" readonly>                            <label for="Price" class="order-label">Price</label>                            <input type="number" id="Price" name="Price" class="input-responsive" value="<?php echo $row['Price']?>" readonly>                            <input type="hidden" id="Price" name="Price" value="<?php echo $row['Price']?>">                            <label for="number" class="order-label">Quantity</label>                            <input type="number" id="number" name="qty" class="input-responsive" value="1" required>                        </div>                    </fieldset>                    <fieldset>                        <legend>Delivery Details</legend>                        <label for="full_name" class="order-label">Full Name</label>                        <input type="text" name="full_name" id="full_name" placeholder="E.g. " class="input-responsive" required>                        <label for="contact" class="order-label">Phone Number</label>                        <input type="tel" id="contact" name="contact" placeholder="E.g. 09843xxxxxx" class="input-responsive" required>                        <label for="email" class="order-label">Email</label>                        <input type="email" name="email" id="email" placeholder="E.g. hi@vijaythapa.com" class="input-responsive" required>                        <label for="address" class="order-label">Address</label>                        <input style="padding: 5px" type="text" name="address" id="address" placeholder="Address" class="input-responsive" required>                        <button type="submit" value="<?php echo $row['ID']?>" name="submit" class="btn btn-primary">Confirm Order</button>                    </fieldset>                </form>                <?php            }        }        ?>    </div></section></body></html>