<?phpinclude './Connect.php';global $conn;if($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['Category'])){    $Category = $_POST['Category'];    $Query = "SELECT * FROM products WHERE Category = '$Category'";    $result = mysqli_query($conn,$Query);    if(mysqli_num_rows($result) > 0){        while ($row = mysqli_fetch_assoc($result)){            ?>            <div id="menu_card"  class="col-lg-4 d-lg-flex gap-5 align-items-center p-3 rounded-4">                <img class="rounded-4" width="140" src="../Model/Product_Images/<?php echo $row['Product_image']?>" alt="burger">                <div>                    <h5  class="fw-bold text-success"><?php echo $row['ProductName']?></h5>                    <p class="text-secondary">                        <?php echo $row['Description']?>                    </p>                    <h6 class="text-secondary fw-bold">Price: â‚± <?php echo $row['Price']?></h6>                    <button id="Add_cart" value="<?php echo $row['ID']?>" class="btn btn-success">Order Now</button>                </div>            </div><?php        }    }else{        echo "<h5 class='text-center'>No Category Available.</h5>";    }}