<?phpinclude './Connect.php';global $conn;if($_SERVER['REQUEST_METHOD'] === 'POST' ||isset($_POST['tracking_order'])){    $track = $_POST['tracking_order'];    $Query = "SELECT OrderId,CostumerName,OrderDate,Quantity,OrderStatus,OrderPrice,Product_Name,TrackingCode FROM orders WHERE TrackingCode = '$track'";    $result = mysqli_query($conn,$Query);    if(mysqli_num_rows($result) > 0){        while ($row = mysqli_fetch_assoc($result)){            // check if the tracking code is valid            ?>            <!-- HTML TABLE-->            <div class="modal fade" id="ViewOrderStatus_modal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">                <div class="modal-dialog modal-xl modal-dialog-centered modal-dialog-scrollable">                    <div class="modal-content">                        <div class="modal-header">                            <h1 class="modal-title fs-5" id="exampleModalLabel">                                <span class="text-danger fw-bold">Hi</span> <?php echo $row['CostumerName']?></h1>                            <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>                        </div>                        <div class="modal-body">                            <table class="table table-responsive-sm table-responsive">                                <thead>                                <tr>                                    <th scope="col">OrderID</th>                                    <th scope="col">Customer Name</th>                                    <th scope="col">Your Order</th>                                    <th scope="col">Order Date</th>                                    <th scope="col">Quantity</th>                                    <th scope="col">Order Status</th>                                    <th scope="col">Total</th>                                     <th scope="col">Action</th>                                </tr>                                </thead>                                <tbody>                                <tr>                                    <td><?php echo $row['OrderId']?></td>                                    <td><?php echo $row['CostumerName']?></td>                                    <td><?php echo $row['Product_Name']?></td>                                    <td><?php echo $row['OrderDate']?></td>                                    <td><?php echo $row['Quantity']?></td>                                    <td  class="fw-bold <?php echo $row['OrderStatus'] === 'Pending' ? 'text-danger' :  ($row['OrderStatus'] === 'Canceled' ? 'text-danger': 'text-success' ) ?> "><?php echo $row['OrderStatus']?></td>                                    <td class="fw-bold"><?php echo number_format($row['OrderPrice']) ?>â‚±</td>                                     <td>                                         <button <?php echo $row['OrderStatus'] === 'Canceled' ? 'disabled true' : ''?>id="Cancel_btn" value="<?php echo $row['OrderId']?>" class="btn btn-danger">                                             <?php echo $row['OrderStatus'] === 'Canceled' ? 'Canceled' : 'Cancel'?>                                         </button>                                     </td>                                </tr>                                </tbody>                            </table>                        </div>                        <div class="modal-footer">                            <button style="background-color: #142D55" type="button" class="btn fw-bold text-light" data-bs-dismiss="modal">Close</button>                        </div>                    </div>                </div>            </div            <?php        }    }else{        echo "<h5 class='text-danger'>Invalid Tracking Code!</h5>";    }}