<?phpinclude './Connect.php';global $conn;if($_SERVER['REQUEST_METHOD'] === 'POST'){    $Query = "SELECT * FROM products";    $result = mysqli_query($conn,$Query);    if(mysqli_num_rows($result) > 0){        while ($row = mysqli_fetch_assoc($result)){            ?><!-- HTML CODE-->            <tr>                <th scope="row"> <?php echo $row['ID'] ?> </th>                <td>                    <img id="Product_image" src="../Model/Product_Images/<?php echo $row['Product_image'] ?>" alt="Product">                </td>                <td> <?php echo $row['ProductName'] ?></td>                <td> <?php echo $row['Category'] ?></td>                <td> <?php echo $row['Price'] ?></td>                <td>                    <button id="btn_view_updateProduct_LIST" value="<?php echo $row['ID'] ?>" style="background-color: #142D55" class=" badge btn text-bg-primary p-2">                       Update                    </button>                    <button id="Delete_product"  value="<?php echo $row['ID'] ?>"  style="background-color: #dc3545" class=" badge btn text-bg-danger p-2">                        Delete                    </button>                    <button id="DisplayProduct_btn"  value="<?php echo $row['ID'] ?>" style="background-color: #142D55" class=" badge btn text-bg-success p-2">                       View                    </button>                </td>            </tr><?php        }    }}