<?phpinclude './Connect.php';global $conn;if($_SERVER['REQUEST_METHOD'] === 'POST'){    $FilePath = $_FILES['UpdatedImage']['tmp_name'];    $FileName = $_FILES['UpdatedImage']['name'];    $FileDir = './CategoryImages/';    $Destination = $FileDir . $FileName;    move_uploaded_file($FilePath,$Destination);    $imageName = htmlspecialchars(basename($_FILES['UpdatedImage']['name']));    $CategoryNameUpdated = $_POST['CategoryName'];    $SAVEId = $_POST['SAVEId'];    $Query = "UPDATE category SET CategoryImage = '$imageName', CategoryName = '$CategoryNameUpdated' WHERE CategoryID = $SAVEId";    mysqli_query($conn,$Query);}