<?phpinclude './Connect.php';global $conn;if($_SERVER['REQUEST_METHOD'] === 'POST'){// handling images upload    $FilePath = $_FILES['CategoryImage']['tmp_name'];    $FileName = $_FILES['CategoryImage']['name'];    $FileDir = './CategoryImages/';    $Destination = $FileDir . $FileName;    move_uploaded_file($FilePath,$Destination);    $imageName = htmlspecialchars(basename($_FILES['CategoryImage']['name']));    $categoryName = $_POST['CategoryName'];    $Query = "INSERT INTO category(CategoryImage,CategoryName)    VALUES ('$imageName','$categoryName')";    mysqli_query($conn,$Query);}