<?phpinclude  './Connect.php';global $conn;if($_SERVER['REQUEST_METHOD'] === 'POST'){    $Query = "SELECT * FROM category";    $result = mysqli_query($conn,$Query);    if(mysqli_num_rows($result) > 0){        while ($row = mysqli_fetch_assoc($result)){            ?>            <tr>                <th scope="row"><?php echo $row['CategoryID']?></th>                <td>                    <img id="categoryImage" src="../Model/CategoryImages/<?php echo $row['CategoryImage']?>" alt="CategoryImage">                </td>                <td><?php echo $row['CategoryName']?></td>                <td>                    <button style="background-color: #142D55;" id="btn_update_category" value=" <?php echo $row['CategoryID']?>" class="btn btn-dark">                        <i class="fa-solid fa-pen-to-square text-light"></i>                    </button>                    <button style="background-color: #142D55;" id="btn_Delete_category" value=" <?php echo $row['CategoryID']?>" class="btn btn-dark">                        <i class="fa-solid fa-trash-can"></i>                    </button>                </td>            </tr><?php        }    }}