<?phpinclude './Connect.php';global $conn;if($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['SearchValue'])){    $SearchValue = $_POST['SearchValue'];    $Query = "SELECT * FROM orders WHERE TrackingCode = '$SearchValue'";    $result = mysqli_query($conn,$Query);    if(mysqli_num_rows($result) > 0){        while ($row = mysqli_fetch_assoc($result)){            ?>            <!-- TR FOR ORDER LIST-->            <tr>                <th scope="row"><?php echo $row['OrderId']?></th>                <td><?php echo $row['CostumerName']?></td>                <td><?php echo $row['ContactNumber']?></td>                <td><?php echo $row['Address']?></td>                <td class="fw-bold         <?php                switch ($row['OrderStatus']){                    case 'Received':                        echo 'text-success';                        break;                    case 'Processing':                        echo 'text-primary';                        break;                    case 'Canceled':                        echo 'text-danger';                        break;                    case 'Pending':                        echo 'text-warning';                        break;                }                ?>             "><?php echo $row['OrderStatus']?></td>                <td><?php echo $row['Payment']?></td>                <td>â‚±<?php echo number_format( $row['OrderPrice']) ?></td>                <td>                    <button id="btn_edit" value="<?php echo $row['OrderId']?>" style="background-color: #142D55;" class="btn">                        <i class="fa-solid fa-pen-to-square text-light"></i>                    </button>                </td>            </tr>            <?php        }    }else{        echo "<h1>No Result</h1>";    }}