<?phpinclude './Connect.php';global $conn;session_start();if($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['btn_logIn'])){    $UserName = $_POST['UserName'];    $Password = $_POST['Password'];    //validating formif(!empty($UserName) || !empty($Password)){    $Query = "SELECT * FROM admin WHERE UserName = '$UserName' AND Password = '$Password'";    $result = mysqli_query($conn,$Query);    $_SESSION['status'] = $UserName;    if(mysqli_num_rows($result) > 0){        echo "successfully Login";        header('Location: ../View/Dashboard.php');    }else{        echo "You are not Registered";        header('Location: ../View/Login.php');    }}else{    header('Location: ../View/Login.php');}}