<?phpinclude "config.php";global $conn;if($_SERVER['REQUEST_METHOD'] == "POST"){    $id = $_POST['id'];    $Query = "SELECT todo_title,todo,status,Description FROM todo_app_tb WHERE ID  = $id";    $result = mysqli_query($conn,$Query);    if(mysqli_num_rows($result) > 0){        while ($row = mysqli_fetch_assoc($result)){            ?>            <div  class="modal fade" id="view_Update" data-bs-backdrop="static" data-bs-keyboard="true" tabindex="-1" aria-labelledby="update_modal" aria-hidden="false">            <div class="modal-dialog">                <div class="modal-content">                    <div class="modal-header border-0 rounded-0"  style="background-color: #2c2c38;">                        <h1 class="modal-title fs-5" id="update_modal">Update Todo</h1>                        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>                    </div>                    <div class="modal-body">                        <label  for="titleSave">Title</label>                        <input type="text" value="<?php echo $row['todo_title'] ?>" class="form-control" id="titleSave" >                        <label  for="todoSave">Todo</label>                        <input type="text" value="<?php echo $row['todo'] ?>" class="form-control" id="todoSave">                        <label  for="DescriptionSave">Description</label>                        <input type="text" value="<?php echo $row['Description'] ?>" class="form-control" id="DescriptionSave">                        <br>                        <select id="statusSave" class="form-select" aria-label="Default select example">                            <option selected><?php echo $row['status']?></option>                            <option value="TODO">TODO</option>                            <option value="DOING">DOING</option>                            <option value="DONE">DONE</option>                        </select>                    </div>                    <div class="modal-footer">                        <button id="btn_Up" value="<?php echo $id?>"  class="btn btn-dark">Update</button>                    </div>                    </div>                </div>            </div><?php        }    }else{        echo "<h1>No Todo Available</h1>";    }}