<?phpinclude '../Connect.php';global $conn;if($_SERVER['REQUEST_METHOD'] === "POST"){    $Tr = "";    $Dates = $_POST['DataDate'];    $Query = "SELECT     collageenroll.ID,     collageenroll.FisrtName,    collageenroll.LastName,    collageenroll.CourseFROM attendancerecord         INNER JOIN collageenroll ON attendancerecord.Student_Id = collageenroll.ID    WHERE attendancerecord.AttendanceDate = '$Dates'";    $result = mysqli_query($conn,$Query);        while ($row = mysqli_fetch_assoc($result)){            $fName = $row['FisrtName'];            $lName = $row['LastName'];            $course = $row['Course'];            $studentId = $row['ID'];            $Tr .= "<tr>";            $Tr .= "<td>$studentId</td>";            $Tr .= "<td>$fName</td>";            $Tr .= "<td>$lName</td>";            $Tr .= "<td>$course</td>";            $Tr .= "</tr>";        }        echo $Tr;}?>