ess">                        </div>                        <div>                            <label for="Birthday">Birthday</label>                            <input value="<?php echo $row['Birthday'] ?>" class="form-control"  type="text" disabled id="Birthday">                        </div>                        <div>                            <label for="Course">Course</label>                            <input value="<?php echo $row['Course'] ?>" class="form-control"  type="text" disabled id="Course">                        </div>                        <div>                            <label for="Year">Year</label>                            <input value="<?php echo $row['Year'] ?>" class="form-control"  type="text" disabled id="Year">                        </div>                        <div>                            <label for="Gender">Gender</label>                            <input value="<?php echo $row['gender'] ?>" class="form-control"  type="text" disabled id="Gender">                        </div>                    </div>                </div>            </div>        </div><?php    }}